INSERT INTO `zhilian_zhilianjob` VALUES ('63', '系统运维工程师(苏州)(J10118)', 'http://jobs.zhaopin.com/639443325250172.htm', '0', '0', '1', '苏州', '本科', 'IT技术支持/维护工程师', '15天前', '交通补助', '<div class=\"tab-inner-cont\">\r\n                        <!-- SWSStringCutStart -->\r\n                        <p><br></p><p><br><span style=\"font-size: 12px;\">工作职责：<br></span></p><p>1、负责大数据、IT支撑产品售后技术支持工作；</p><p>2、参与大数据、IT支撑产品的测试、实施和验收等工作；</p><p>3、负责复杂故障处理、故障分析、调优、缺陷跟踪等工作；</p><p>4、负责产品巡检、备份、调优、监控、应急演练、重点保障等方案的制定；</p><p><span style=\"font-size: 12px;\">任职资格：<br></span></p><p>学历要求：国民教育序列本科及以上学历，个别优秀的学历可放宽至函授本科；</p><p>                   5年及以上工作经验，个别优秀的，经验可放宽至3年；</p><p>                   计算机软件、数据库、大数据、网络、通信等相关专业；</p><p> </p><p> 技能要求:</p><p>1、精通IT类软硬件（服务器、存储、数通网络、操作系统、数据库、IT应用等专业的一种或多种）运维，具有相关工作三年以上经验；</p><p>2、熟悉主流Linux操作系统，centos，redhat等；</p><p>3、能够熟练使用至少1门脚本编程语言，如shell、python等；</p><p>4、熟悉软件产品的实施交付、技术支持等工作，有软件开发经验者优先；</p><p>能力要求</p><p>1、良好的自我管理能力和学习能力；</p><p>2、良好的沟通能力和文档编写能力；</p><p>3、良好的语言表达能力和理解能力；</p><p>4、良好的现网安全分析意识，责任心强；</p><p>5、能够承受较大工作压力；</p><p>工作地点：苏州</p><p><span style=\"font-size: 12px;\"></span><br></p>\r\n                        <!-- SWSStringCutEnd -->\r\n                        \r\n                        <b>工作地址：</b>\r\n                        <h2>\r\n                            苏州市高新区科技城昆仑山路58号中移软件园\r\n                            \r\n                        </h2>\r\n                        \r\n                        \r\n                        <p>\r\n                            <button id=\"applyVacButton1\" class=\"button-small\" title=\"申请职位\" onclick=\"zlzp.searchjob.ajaxApplyBrig3(\'1\');dyweTrackEvent(\'bjobsdetail14gb\',\'directapply_middle\');\"></button>\r\n                        </p>\r\n                        \r\n                    </div>', '苏州市高新区科技城昆仑山路58号中移软件园', '中移(苏州)软件技术有限公司', '2017-09-05 06:42:32.000000', '999');
