INSERT INTO `zhilian_zhilianjob` VALUES ('65', '安全运维工程师', 'http://jobs.zhaopin.com/672265623250199.htm', '15000', '8000', '1', '苏州', '本科', 'IT技术支持/维护工程师', '15天前', '五险一金', '<div class=\"tab-inner-cont\">\r\n                        <!-- SWSStringCutStart -->\r\n                        <p>职位描述：</p><p><br></p><p>服务器与网络基础设备的安全加固；</p><p><br></p><p>安全事件排查与分析，配合定期编写安全分析报告，专注业内安全事件； </p><p><br></p><p>跟踪最新漏洞信息，进行业务产品的安全检查； </p><p><br></p><p>负责信息安全策略/流程的制定,安全培训/宣传及推广；</p><p><br></p><p>负责Web漏洞和系统漏洞修复工作推进，跟踪解决情况，问题收集。</p><p><br></p><p><br></p><p>职位要求：</p><p><br></p><p>熟悉主流的Web安全技术，包括SQL注入、XSS、CSRF等OWASP TOP 10安全风险； </p><p><br></p><p>熟悉Linux/Windows下系统和软件的安全配置与加固；</p><p><br></p><p>熟悉常见的安全产品及原理，例如IDS、IPS、防火墙,WAF等；</p><p><br></p><p>掌握常见系统、应用的日志分析方法；</p><p><br></p><p>熟练掌握C/PHP/Perl/Python/Shell等1或多种语言； </p><p><br></p><p>具有安全事件挖掘、调查取证经验； </p><p><br></p><p>网络基础扎实，熟悉TCP/IP协议，二层转发和三层路由的原理，动态路由协议，常用的应用层协议；</p><p><br></p><p><span style=\"line-height: 1.5;\">较好的文档撰写能力、语言表达和与沟通能力。</span><br></p>\r\n                        <!-- SWSStringCutEnd -->\r\n                        \r\n                        <b>工作地址：</b>\r\n                        <h2>\r\n                            苏州工业园区若水路388号纳米大学科技园\r\n                            \r\n                            <a href=\"javascript:fnOpenMiniMap(\'\',\'\',\'%E8%8B%8F%E5%B7%9E\',\'31.274775\',\'120.740431\');\" onclick=\"dyweTrackEvent(\'bjobsdetail14gb\', \'showjobmap\');\" class=\"see-map\">查看职位地图</a>\r\n                            \r\n                        </h2>\r\n                        \r\n                        \r\n                        <p>\r\n                            <button id=\"applyVacButton1\" class=\"button-small\" title=\"申请职位\" onclick=\"zlzp.searchjob.ajaxApplyBrig3(\'1\');dyweTrackEvent(\'bjobsdetail14gb\',\'directapply_middle\');\"></button>\r\n                        </p>\r\n                        \r\n                    </div>', '苏州工业园区若水路388号纳米大学科技园', '苏州奖多多科技有限公司 ', '2017-09-05 06:42:40.000000', '1');
